#include <stdint.h>
#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <unistd.h>
#include <pthread.h>

char word[] = "";

char* word_two;

void* work1(void* arg) {
  int a = *((int *) arg);

  // printf("\na = %d\n", a);
  for (int i = 0; i < strlen(word_two); i++){
    if (a == 1 && !(i % 2)) {       //evens and first thread
      word_two[i] = word_two[i] + 1;
      fprintf(stdout,"%c", word_two[i]);
    } else if (a == 2 && (i % 2)) { //odds and second thread
      word_two[i] = word_two[i] + 2;
      fprintf(stdout,"%c", word_two[i]);
    }
  }
  return NULL;
}

int main()
{
  // allocate and fill heap array
  word_two = (char *)malloc(strlen(word));
  strcpy(word_two, word);                        

  pthread_t ptid1, ptid2; 
  int a = 1, b = 2;

  pthread_create(&ptid1, NULL, work1, (void *) &a);
  pthread_create(&ptid2, NULL, work1, (void *) &b);

  pthread_join(ptid1, NULL);
  pthread_join(ptid2, NULL);

  return 1;
}